import { Component, OnInit } from '@angular/core';
import {FormControl, FormGroup} from "@angular/forms";
import {ActivatedRoute, Router} from "@angular/router";
import {UtilizatorService} from "../utilizator.service";
import {Utilizator} from "../utilizator";
import {HttpErrorResponse} from "@angular/common/http";

@Component({
  selector: 'app-edit',
  templateUrl: './edit.component.html',
  styleUrls: ['./edit.component.css']
})
export class EditComponent implements OnInit {
    private oldName:string="";
    private oldPass:string="";
    private oldSalar:string="";
    private userId:number=0;
    editGrup = new FormGroup({
    nume : new FormControl(''),
    password : new FormControl(''),
    salar : new FormControl(''),});

  constructor(
    private route: ActivatedRoute,
    private serv: UtilizatorService,
    private router:Router,
  ) { }
  homePage():void{
    this.router.navigateByUrl("");
  }
  submitForm():void{
    // console.warn(this.addGrup.value.nume);
    this.serv.addUser(this.editGrup.value.nume,this.editGrup.value.password,this.editGrup.value.salar).subscribe((data) =>console.log(data));
    this.homePage();
  }
  public getOneUser(idUser:number):void{
    console.warn("ia utilizator din lista")
    this.serv.asyncCall().
    this.serv.getUser(idUser).subscribe((data)=>{
      this.oldName=data.nume;
        this.oldPass=data.password;
        this.oldSalar=data.salar;
        console.warn(this.oldName)
    });
    console.warn(this.oldName);
  }
  ngOnInit(): void {
    this.userId = Number(this.route.snapshot.paramMap.get('id'));
    this.getOneUser(this.userId);
    //console.warn(this.oldUser.nume)
    console.warn(this.oldName);
    this.editGrup.patchValue({
      nume:this.oldName,
      password:this.oldPass,
      salar:this.oldSalar,
    })
    // this.route.queryParams.subscribe(params => {
    //   this.nume = params['name'];
    // });
  }

}
